mapper.insert:
INSERT INTO trans_rcp_notice_mas (
    payday /*은행전문 수납일자*/
  , paytime /*수납시간*/
  , rcpamt /*수납금액*/
  , rcpusrname /*출금계좌 성명*/
  , chacd /*기관코드*/
  , vano /*가상계좌번호*/
  , svecd /*수납방법코드 (VAS-가상계좌, DCS-창구현금(현장), DCD-창구카드(현장), DVA-기관계좌입금*/
  , rcpmasst /*수납상태*/
  , tran_tot_cnt /*수납통지 총 발생횟수*/
  , tran_fst_dt /*수납통지 최초발송일시*/
  , tran_last_dt /*수납통지 최종발송일시*/
  , rcpmascd /*수납원장코드*/
  , tran_succ_yn /*수납통지 성공여부: Y(성공), N(실패)*/
)
VALUES ( #{payDay}
       , #{payTime}
       , #{rcpAmt}
       , #{rcpUsrName}
       , #{chaCd}
       , #{vano}
       , #{svecd}
       , #{rcpMasSt}
       , #{tranTotCnt}
       , now()
       , now()
       , #{rcpMasCd}
       , #{tranSuccYn}
       )

INSERT INTO trans_rcp_notice_hist (
        rcpmascd /*수납원장코드*/
      , rcpmasst /*수납상태코드 */
      , tran_cnt /*수납원장코드드별 수납통지 순번*/
      , tran_succ_yn /*수납통지 성공여부: Y(성공), N(실패)*/
      , tran_dt  /*수납통지 발송일시*/
)
VALUES ( #{rcpMasCd}
       , #{rcpMasSt}
       , #{tranCnt}
       , #{tranSuccYn}
       , now()
       )

mapper.select:
SELECT rcp_notice_url
FROM xcha_rcp_noti_info
WHERE chacd = #{chacd}
